import React from "react";
import BookDetails from "./BookDetails";

class Details extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      books: [],
      category: "JavaScript"
    };
  }

  componentDidMount() {
    this.setState({ category: this.props.category });
  }

  componentDidUpdate() {
    // this.setState({ category: this.props.category });
    // this.makeFetch(this.props.category);
  }

  update(books) {
    this.setState({ books: books });
  }

  updateCategory = () => {
    this.setState({ category: this.props.category });
  };

  makeFetch() {
    const category = this.state.category;
    let url = `https://www.googleapis.com/books/v1/volumes?q=${category}`;
    fetch(url)
      .then(response => response.json())
      .then(jsonResponse => this.update(jsonResponse.items));
  }

  render() {
    this.updateCategory();
    const books = this.state.books;
    const listItems = books.map(book => (
      <BookDetails bookDetails={book} key={book.id} />
    ));
    return (
      <div>
        <h2>Book search for {this.props.category}</h2>
        <div className="row">{listItems}</div>
      </div>
    );
  }
}

export default Details;
