import React from "react";
import { Link } from "react-router-dom";
import DropdownItem from "./DropdownItem";

class MenuListItem extends React.Component {
  navClick = () => {
    this.props.search(this.props.text);
  };

  render() {
    const shouldBeActive = this.props.isActive;
    const subcategories = this.props.subcategories.map(item => {
      console.log(item);
      return <DropdownItem item={item} key={item.id} />;
    });
    if (shouldBeActive) {
      return (
        <li className="nav-item dropdown">
          <a className="nav-link dropdown-toggle active" href="#">
            {this.props.text}
          </a>
          <div class="dropdown-menu" aria-labelledby="navbarDropdown">
            {subcategories}
          </div>
        </li>
      );
    } else {
      return (
        <li className="nav-item dropdown">
          <a className="nav-link dropdown-toggle" href="#">
            {this.props.text}
          </a>
        </li>
      );
    }
  }
}

export default MenuListItem;
