import React from "react";
import noimage from "../noimage.png";

class Book extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      book: undefined
    };
  }

  componentDidMount() {
    fetch(this.props.bookUrl)
      .then(response => response.json())
      .then(responseJson => {
        console.log(responseJson);
        this.setState({ book: responseJson });
      });
  }

  render() {
    const book = this.state.book;
    if (book === undefined) {
      return (
        <div className="spinner-border" role="status">
          <span className="sr-only">Loading...</span>
        </div>
      );
    } else {
      return (
        <div className="row">
          <div className="col-md-6">
            <img
              src={this.book.volumeInfo.imageLinks.thumbnail}
              alt={noimage}
            />
          </div>
          <div className="col-md-6">
            <p>Title: {this.book.volumeInfo.title}</p>
            <p>Pages: {this.book.volumeInfo.pageCount}</p>
          </div>
        </div>
      );
    }
  }
}

export default Book;
