import React from "react";
import { Link } from "react-router-dom";
import DropdownItem from "./DropdownItem";

class MenuListItem extends React.Component {
  navClick = () => {
    this.props.search(this.props.text);
  };

  render() {
    const shouldBeActive = this.props.isActive;
    const subcategories = this.props.subcategories.map(item => {
      <DropdownItem name={item.name} key={item.id} />;
    });
    if (shouldBeActive) {
      return (
        <li className="nav-item dropdown">
          <a className="nav-link dropdown-toggle active" href={this.props.url}>
            {this.props.text}
          </a>
          <div class="dropdown-menu" aria-labelledby="navbarDropdown">
            <a class="dropdown-item" href="#">
              Action
            </a>
            <a class="dropdown-item" href="#">
              Another action
            </a>
            <div class="dropdown-divider"></div>
            <a class="dropdown-item" href="#">
              Something else here
            </a>
          </div>
        </li>
      );
    } else {
      return (
        <li className="nav-item dropdown">
          <a className="nav-link dropdown-toggle" href={this.props.url}>
            {this.props.text}
          </a>
        </li>
      );
    }
  }
}

export default MenuListItem;
